def findZigZagSequence(a, n):
    a.sort()
    mid = int((n + 1)/2 -1) #@@
    a[mid], a[n-1] = a[n-1], a[mid]
    # bring the larget element to the middle
    # since list is sorted till mid all values will be as expectd, after middle value
    # numbers increase to mid and then decrease from mid to the end

    # st = mid + 1
    # ed = n - 1

    st = mid + 1
    ed = n-2  #@@ mid +2
    while(st <= ed):
        a[st], a[ed] = a[ed], a[st]
        st = st + 1
        ed = ed - 1  #@@ 

    for i in range (n):
        if i == n-1:
            print(a[i])
        else:
            print(a[i], end = ' ')
    return a
